UINT32 cu_range2mask(int f, int t)
{
        UINT32 mask;

        mask = 0;

/* Assuming 1 port based */
        if (f <= t) {
                for (; f <= t; f++)
                        mask = mask | (1 << (f-1));
        } else {
                for (; t <= f; t++)
                        mask = mask | (1 << (t-1));
        }
        return mask;
}

void cu_mask2range(int* f, int* t, UINT32 mask)
{
        int i;
        for (i = 0; i < 32; i++, mask = mask >> 1) {
                if ((mask  & 1) == 0)
                        continue;
                *f = i + 1; /* start from 1 not 0 */
                break;
        }

        for (; i < 32; i++, mask = mask >> 1) {
                if ((mask  & 1) == 1)
                        continue;
                *t = i; /* last one, don't add 1 */
                break;
        }
}

