void set_cmd_filter_flag(
        struct node *sptr_node,
        char *keyword,
        unsigned int type,
        unsigned int flag,
        unsigned int enable)
{
        if (((sptr_node->type & ~(PARSER_NODE_MASK_BITS)) == type)
        &&  (strcmp(sptr_node->keyword, keyword) == 0))
        {
                if (enable)
                        sptr_node->type |=  flag;
                else
                        sptr_node->type &= ~flag;
        }
}

void set_cmd_filter_type_flag(
        struct node *sptr_node,
        unsigned int type,
        unsigned int flag,
        unsigned int enable)
{
        if (((sptr_node->type & ~(PARSER_NODE_MASK_BITS)) == type)
        &&  (sptr_node->keyword == NULL || sptr_node->keyword[0] == 0 ))
        {
                if (enable)
                        sptr_node->type |=  flag;
                else
                        sptr_node->type &= ~flag;
        }
}

