void get_222_ver(char *ver, UINT32 *v1, UINT32 *v2, UINT32 *v3)
{
        int len;

        *v1 = *v2 = *v3 = 0;
        *v1 = cli_get_int(ver, 0, &len);
        ver += len;
        *v2 = cli_get_int(ver, 0, &len);
        ver += len;
        *v3 = cli_get_int(ver, 0, &len);
}

